        program signifc---    Evaluate significance of a Poisson signal detection.	integer npmax, nmax        parameter (npmax=250, nmax=200)        integer n_on, n_off, iter        real*8 t_on, t_off, shi, ps        real*8 c(0:nmax), sigma, ginv        write(*,*) 'Enter s+b counts and time:'        read(*,*) n_on, t_on        write(*,*) 'Enter b counts and time:'        read(*,*) n_off, t_off        call setc(n_on, t_on, n_off, t_off, c, nmax)        call sigsig(n_on, t_on, n_off, t_off, c, nmax, shi, ps, iter)        sigma = ginv(0.5d0+ps/2.d0, 1.d0)        if (iter .le. 0) write(*,*) 'sigsig failed!'        write(*,30) iter, ps, sigma30      format('iter = ',i5,/,     1      'Signal is significant with probability ',1pg13.4,     2      ' = ',1pg13.4,' sigma.')        close(1)        endc---------------------------------------------------------------------        function ginv(p, sig)c---    This function is essentially an approximate inverse error c       function, returning the value of x such that the integratedc       probability of a Gaussian with standard deviation sig isc       equal to p. (Integrated from - infinity.)c       This rational approximation is stolen from Abromowitz and c       Stegun...        real*8 ginv, p, sig        real*8 c0, c1, c2, d1, d2, d3, t, sign, pp        common /Qic/ c0, c1, c2, d1, d2, d3        data c0,c1,c2/2.5155170d0, .8028530d0, .0103280d0/        data d1,d2,d3/1.4327880d0, .1892690d0, .0013080d0/        pp = p        sign = -1.d0        if (p .gt. 0.5) then            sign = - sign            pp = 1.d0 - pp        endif        t = sqrt(-2.d0*log(pp))        ginv = c0 + c1*t + c2*t**2        ginv = ginv / (1.0 + d1*t + d2*t**2 + d3*t**3)        ginv = t - ginv        ginv = sign * sig * ginv        return        end